module.exports=add;
function add(user_id, globalVariable,callback){
	if(globalVariable[user_id].request.query.name){
			globalVariable.database.mongoDB.collection("rooms_list").update({ name:globalVariable[user_id].request.query.name},{ name:globalVariable[user_id].request.query.name},{upsert:true},function(){ });
		return callback('updated');
	}
	else{
		return callback('error');
	}
	
}