module.exports=list;
function list(globalVariable){
	globalVariable.database.mongoDB.collection("rooms_list").find({}).toArray(function(err, result) {
		if(err){
			globalVariable.event.emit("accessories","listRooms:obtained",err);
			
		}else{
			globalVariable.event.emit("accessories","listRooms:obtained",JSON.stringify(result));
			
		}
	});
}