module.exports=update;
function update(user_id, globalVariable,callback){
	if(globalVariable[user_id].request.query.name && globalVariable[user_id].request.query.id){
		globalVariable.database.mongoDB.collection("rooms_list").update({_id: new globalVariable.database.ObjectID(globalVariable[user_id].request.query.id)},{ name:globalVariable[user_id].request.query.name},{upsert:true},function(){ return callback('updated');});
		
	}
	else{
		return callback('error');
	}
	
}